import React, { Component } from "react";
import { Button } from "react-bootstrap";

class Counter3 extends Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }
  handleIncreament = () => {
    this.setState((data) => ({ count: data.count + 1 }));
  };
  handledecreament = () => {
    this.setState((data) => ({ count: data.count - 1 }));
  };
  render() {
    return (
      <div>
        <h3>Count : {this.state.count}</h3>
        <Button
          onClick={() => this.state.count < 10 && this.handleIncreament()}
        >
          Increament
        </Button>
        <Button onClick={() => this.state.count > 0 && this.handledecreament()}>
          Decreament
        </Button>
      </div>
    );
  }
}
export default Counter3;
