import React, { Component } from "react";

export class CounterClass extends Component {
  constructor() {
    super();
    this.state = {
      count: 0,
    };
  }
  UpdateCount() {
    this.setState({
      count: this.count + 1,
    });
  }
  render() {
    return (
      <div>
        <button>
          Count :onClick={" "}
          {() => {
            this.UpdateCount();
          }}{" "}
        </button>
      </div>
    );
  }
}
