import React, { Component } from "react";
import { Button } from "react-bootstrap";
import Counter3Child from "./Counter3Child";

class Counter3 extends Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }
  handleIncreament = () => {
    this.setState((data) => ({ count: data.count + 1 }));
  };
  handledecreament = () => {
    this.setState((data) => ({ count: data.count - 1 }));
  };
  handleReset = () => {
    this.setState((data) => ({ count: data.count * 0 }));
  };
  render() {
    return (
      <div>
        {/* <h3>Count : {this.state.count}</h3> */}
        <Counter3Child
          ChildShow={this.state.count}
          buttonShow={this.handleIncreament}
        />
        {/* <Button
         buttonShow={this.state.count}
        >
          Increament
        </Button> */}
        <Button onClick={() => this.state.count > 0 && this.handledecreament()}>
          Decreament
        </Button>
        <Button onClick={this.handleReset}>Reset</Button>
      </div>
    );
  }
}
export default Counter3;
