import React, { Component } from "react";

export class Counter extends Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }
  handleIncreament = () => {
    this.setState((Anyname) => ({ count: Anyname.count + 1 }));
  };
  handleDecreament = () => {
    this.setState({ count: Anyname.count - 1 });
  };
  render() {
    return (
      <div>
        <h2>Count Class :{this.state.count}</h2>
        <button onClick={this.handleIncreament}>Increment</button>
        <button onClick={() => this.handleDecreament()}>Decrement</button>
      </div>
    );
  }
}
